import {Routes,  Route} from "react-router-dom";
import React from 'react';  // нужно потому что тут пишем jsx код

import GlobalFeed from 'pages/globalFeed'; //указываю абсолютный путь, потому что все уже в папке node_modules
import Article from 'pages/article';
import Authentication from 'pages/authentication'

export default () => {
	return (
		<Routes>
			<Route path='/' element={<GlobalFeed/>} />
			<Route path='/articles/:slug' element={<Article/>} />

			<Route path='/login' element={<Authentication/>} />
			<Route path='/register' element={<Authentication/>} />
		</Routes>
	)
}

// <Routes> определяет на каком мы Route и какой Route нам необходим
//  :slug это алиас article, т.е. то что мы видим в url

// Switch определяет на каком мы роуте и какой роут нам необходим